mapper.select:
select count(1)
from auth_func
where parent_id = #{parentId}

SELECT DISTINCT f.func_id, f.func_name, f.func_key, f.func_type, f.func_icon, f.parent_id, f.func_sort, f.func_dir_status FROM auth_user u INNER JOIN auth_user_role ur ON u.user_id = ur.user_id INNER JOIN auth_role r ON ur.role_id = r.role_id INNER JOIN auth_role_func rf ON r.role_id = rf.role_id INNER JOIN auth_func f ON rf.func_id = f.func_id
AND u.user_id = #{userId}
AND f.func_type = #{funcType}
ORDER BY f.func_sort ASC
